mapper.select:
select a.academic_id,a.title,a.status,a.authors,a.details,a.per_num,a.per_contribution
from academic a
where a.r_id = #{r_id} and a.a_index = #{index}

select c.competition_id,c.title,c.award,c.rank,c.per_num
from competition c
where c.r_id = #{r_id} and c.c_index = #{index}

select c.course_id,c.title,c.grade
from course c
where c.r_id = #{r_id} and c.c_index = #{index}

select i.intellectual_id,i.title,i.status,i.authors,i.type,i.per_num,i.per_contribution
from intellectual i
where i.r_id = #{r_id} and i.i_index = #{index}

select r.research_id,r.title,r.chapter,r.status,r.authors,r.per_num,r.per_contribution
from research r
where r.r_id = #{r_id} and r.r_index = #{index}

select s.service_id,s.type,s.content,s.status,s.spend_time,s.per_num,s.per_contribution
from service s
where s.r_id = #{r_id} and s.s_index = #{index}

select s.sug_id,s.content
from suggestion s
where s.r_id = #{r_id} and s.s_index = #{index}

select t.technology_id,t.project_name,t.module_name,t.code_amount,t.commit_bug,t.resolve_bug
from technology t
where t.r_id = #{r_id} and t.t_index = #{index}

mapper.update:
update course
set
    title = #{course.title},
    grade = #{course.grade}
where c_index = #{course.c_index} and r_id = #{course.r_id}

update suggestion
SET content = #{suggestion.content}
where s_index = #{suggestion.s_index} and r_id = #{suggestion.r_id}

mapper.select:
select report.r_id
from report
where report.type = #{type} and report.time = #{time}

select report.status
from report
where report.type = #{type} and report.time = #{time}
